(624, 172, 'title', '法人税務顧問'),
(625, 172, '_title', 'field_63c64b68cd9cf'),
(626, 172, 'icon', '24'),
(627, 172, '_icon', 'field_63c64bcacd9d0'),
(628, 172, 'description', '継続的な顧問業務を通じて、企業の経営課題を共有し、そして解決することによって、お客様の永続的発展に貢献し\r\nます。また、適正な税務処理や節税のアドバイスを適時的確に行い、お客様の資産保全に貢献します。'),
(629, 172, '_description', 'field_63c64bddcd9d1'),
(630, 172, 'image', '28'),
(631, 172, '_image', 'field_63c64bfccd9d2'),
(632, 172, 'targets', '3'),
(633, 172, '_targets', 'field_63c64c06cd9d3'),
(634, 172, 'advantages_0_advantage_title', '経営に関するアドバイスや提案を行います。'),
(635, 172, '_advantages_0_advantage_title', 'field_63c64c2acd9d5'),
(636, 172, 'advantages_0_advantage_description', '会計監査業務を通じて業績分析を行い、現状の経営課題を抽出し、お客様と経営課題を共有します。そしてその解決方法をお客様とともに考え、また税務のプロとして、アドバイスや提案をさせていただきます。'),
(637, 172, '_advantages_0_advantage_description', 'field_63c64c3ecd9d6'),
(638, 172, 'advantages_1_advantage_title', '決算予測や決算対策をしっかり行います。'),
(639, 172, '_advantages_1_advantage_title', 'field_63c64c2acd9d5'),
(640, 172, 'advantages_1_advantage_description', '決算予測を行い、今期の業績着地を把握し、納税額を予測するとともに、適切な節税方法の提案や納税準備のための資金繰り等への対応を行います。'),
(641, 172, '_advantages_1_advantage_description', 'field_63c64c3ecd9d6'),
(642, 172, 'advantages_2_advantage_title', '資金繰りや資金調達の提案を行います。'),
(643, 172, '_advantages_2_advantage_title', 'field_63c64c2acd9d5'),
(644, 172, 'advantages_2_advantage_description', '業績や財務内容を基に、今後の事業運営、設備投資等に必要な資金を把握し、借入額や返済期間を検討します。また必要に応じて、具体的な資金調達の方法を提案します。'),
(645, 172, '_advantages_2_advantage_description', 'field_63c64c3ecd9d6'),
