(1307, 172, 'targets_0_target', '経営に関するアドバイスや提案が聞きたい'),
(1308, 172, '_targets_0_target', 'field_63c6546bc509b'),
(1309, 172, 'targets_1_target', '納税予測や節税による税金対策をしっかりと行いたい'),
(1310, 172, '_targets_1_target', 'field_63c6546bc509b'),
(1311, 172, 'targets_2_target', '資金繰りや資金調達についても相談したい'),
(1312, 172, '_targets_2_target', 'field_63c6546bc509b'),
(1313, 214, 'title', '法人税務顧問'),
(1314, 214, '_title', 'field_63c64b68cd9cf'),
(1315, 214, 'icon', '24'),
(1316, 214, '_icon', 'field_63c64bcacd9d0'),
(1317, 214, 'description', '継続的な顧問業務を通じて、企業の経営課題を共有し、そして解決することによって、お客様の永続的発展に貢献し\r\nます。また、適正な税務処理や節税のアドバイスを適時的確に行い、お客様の資産保全に貢献します。'),
(1318, 214, '_description', 'field_63c64bddcd9d1'),
(1319, 214, 'image', '28'),
(1320, 214, '_image', 'field_63c64bfccd9d2'),
(1321, 214, 'targets', '3'),
(1322, 214, '_targets', 'field_63c64c06cd9d3'),
(1323, 214, 'advantages_0_advantage_title', '経営に関するアドバイスや提案を行います。'),
(1324, 214, '_advantages_0_advantage_title', 'field_63c64c2acd9d5'),
(1325, 214, 'advantages_0_advantage_description', '会計監査業務を通じて業績分析を行い、現状の経営課題を抽出し、お客様と経営課題を共有します。そしてその解決方法をお客様とともに考え、また税務のプロとして、アドバイスや提案をさせていただきます。'),
(1326, 214, '_advantages_0_advantage_description', 'field_63c64c3ecd9d6'),
(1327, 214, 'advantages_1_advantage_title', '決算予測や決算対策をしっかり行います。'),
(1328, 214, '_advantages_1_advantage_title', 'field_63c64c2acd9d5'),
(1329, 214, 'advantages_1_advantage_description', '決算予測を行い、今期の業績着地を把握し、納税額を予測するとともに、適切な節税方法の提案や納税準備のための資金繰り等への対応を行います。'),
(1330, 214, '_advantages_1_advantage_description', 'field_63c64c3ecd9d6'),
(1331, 214, 'advantages_2_advantage_title', '資金繰りや資金調達の提案を行います。'),
(1332, 214, '_advantages_2_advantage_title', 'field_63c64c2acd9d5'),
(1333, 214, 'advantages_2_advantage_description', '業績や財務内容を基に、今後の事業運営、設備投資等に必要な資金を把握し、借入額や返済期間を検討します。また必要に応じて、具体的な資金調達の方法を提案します。'),
(1334, 214, '_advantages_2_advantage_description', 'field_63c64c3ecd9d6'),
(1335, 214, 'advantages', '3'),
(1336, 214, '_advantages', 'field_63c64c13cd9d4'),
(1337, 214, 'steps_step_tite', ''),
(1338, 214, '_steps_step_tite', 'field_63c64c69cd9d8'),
(1339, 214, 'steps_step_subtitle', ''),
(1340, 214, '_steps_step_subtitle', 'field_63c64c86cd9d9'),
