(319, 1, '2023-01-19 07:14:14', '2023-01-19 07:14:14', '<table>\r\n<tbody>\r\n<tr>\r\n<th>お名前<span class=\"required\">必須</span></th>\r\n<td>[mwform_text name=\"firstname\" size=\"60\" placeholder=\"姓\"][mwform_text name=\"lastname\" size=\"60\" placeholder=\"名\"]</td>\r\n</tr>\r\n<tr>\r\n<th>所属団体（社名等）</th>\r\n<td>[mwform_text name=\"company\" size=\"60\"]</td>\r\n</tr>\r\n<tr>\r\n<th>E-mail<span class=\"required\">必須</span></th>\r\n<td>[mwform_text name=\"email\" size=\"60\" placeholder=\"例） example@gmail.com\"]</td>\r\n</tr>\r\n<tr>\r\n<th>E-mail（確認）<span class=\"required\">必須</span></th>\r\n<td>[mwform_text name=\"emailconfirm\" size=\"60\" placeholder=\"例） example@gmail.com\"]</td>\r\n</tr>\r\n<tr>\r\n<th>お電話番号</th>\r\n<td>[mwform_text name=\"tel\" size=\"60\" placeholder=\"例） 000-1111-2222\"]</td>\r\n</tr>\r\n<tr>\r\n<th>ご相談内容<span class=\"required\">必須</span></th>\r\n<td>[mwform_textarea name=\"message\" cols=\"50\" rows=\"5\"]</td>\r\n</tr>\r\n</tbody>\r\n</table>\r\n<div class=\"l-btn l-btn--2btn c-btn__contact\">\r\n<div class=\"c-btn c-btn--clear\"><input name=\"\" type=\"reset\" value=\"リセット\" /></div>\r\n<div class=\"c-btn c-btn__a\">[mwform_bconfirm value=\"confirm class=\"c-btn__submit\" \"]入力内容を確認する[/mwform_bconfirm]</div>\r\n</div>\r\n<div class=\"l-btn l-btn--2btn c-btn__confirm\">\r\n<div class=\"c-btn c-btn__a\">[mwform_bback value=\"back\" class=\"c-btn__submit\"]前の画面に戻る[/mwform_bback]</div>\r\n<div class=\"c-btn c-btn__a\">[mwform_bsubmit name=\"submit\" class=\"c-btn__submit\" value=\"send\"]送信する[/mwform_bsubmit]</div>\r\n</div>', 'Contact', '', 'publish', 'closed', 'closed', '', 'test', '', '', '2023-01-19 08:38:35', '2023-01-19 08:38:35', '', 0, 'http://localhost/task14/?post_type=mw-wp-form&#038;p=319', 0, 'mw-wp-form', '', 0),
